    // def c4, 1, 0, 0, 0
    // dcl_position v0
    // dcl_texcoord v1
    // dcl_position o0
    // dcl_texcoord o1.xy
    // dp4 o0.x, v0, c0
    o.position.x = dot(i.position, (view_proj_matrix._m00_m10_m20_m30));
    // dp4 o0.y, v0, c1
    o.position.y = dot(i.position, (view_proj_matrix._m01_m11_m21_m31));
    // dp4 o0.w, v0, c3
    o.position.w = dot(i.position, (view_proj_matrix._m03_m13_m23_m33));
