sampler2D depth : register(ps_3_0, s0);
sampler2D diffuse_texture : register(ps_3_0, s1);
float3 ref_end_amount_fade : register(ps_3_0, c0);
struct PixelMain_Input
{
    float2 texcoord : TEXCOORD;
    float4 texcoord1 : TEXCOORD1;
    float4 texcoord2 : TEXCOORD2;
};

float4 PixelMain(PixelMain_Input i) : COLOR
{
    float4 out_color;
    float4 temp0, temp1;
    // def c1, 0.2, 0, 0, 0
    // dcl_texcoord v0.xy
    // dcl_texcoord1 v1
    // dcl_texcoord2 v2
    // dcl_2d s0
    // dcl_2d s1
    // texld r0, v0, s1
    temp0 = tex2D(diffuse_texture, i.texcoord.xy);
    // texld r1, v1, s1
    temp1 = tex2D(diffuse_texture, i.texcoord1.xy);
    // add r0.xyz, r0, r1
    temp0.xyz = temp0.xyz + temp1.xyz;
    // texld r1, v1.zwzw, s1
    temp1 = tex2D(diffuse_texture, i.texcoord1.zw);
    // add r0.xyz, r0, r1
    temp0.xyz = temp0.xyz + temp1.xyz;
    // texld r1, v2, s1
    temp1 = tex2D(diffuse_texture, i.texcoord2.xy);
    // add r0.xyz, r0, r1
    temp0.xyz = temp0.xyz + temp1.xyz;
    // texld r1, v2.zwzw, s1
    temp1 = tex2D(diffuse_texture, i.texcoord2.zw);
    // add r0.xyz, r0, r1
    temp0.xyz = temp0.xyz + temp1.xyz;
    // mul oC0.xyz, r0, c1.x
    out_color.xyz = temp0.xyz * float3(0.2, 0.2, 0.2);
    // texld r0, v0, s0
    temp0 = tex2D(depth, i.texcoord.xy);
    // rcp r0.y, c0.x
    temp0.y = 1.0f / ref_end_amount_fade.x;
    // mul oC0.w, r0.y, r0.x
    out_color.w = temp0.y * temp0.x;
    // 

    return out_color;
}

