    // dcl_2d s0
    // dcl_2d s1
    // dcl_2d s2
    // texld r0, v0, s2
    temp0 = tex2D(self_illumination_texture, texcoord.xy);
    // texld r1, v0, s1
    temp1 = tex2D(diffuse_texture, texcoord.xy);
    // add r0.xyz, r0, -r1
    temp0.xyz = temp0.xyz + -temp1.xyz;
    // dp3 r0.x, r0, r0
    temp0.x = dot(temp0.xyz, temp0.xyz);
    // rsq r0.x, r0.x
    temp0.x = 1 / sqrt(temp0.x);
    // rcp r0.x, r0.x
    temp0.x = 1.0f / temp0.x;
    // add r0.x, -r0.x, c1.x
    temp0.x = -temp0.x + float1(1);
    // texld r2, v0, s0
