    temp0.z = saturate(temp0.z * float1(4));
    // mad_sat r0.x, r0.x, r0.x, r0.z
    temp0.x = saturate(temp0.x * temp0.x + temp0.z);
    // mov r1.x, c1.x
    temp1.x = float1(1);
    // add r0.z, r1.x, -c0.z
    temp0.z = temp1.x + -ref_end_amount_fade.z;
    // rcp r0.z, r0.z
    temp0.z = 1.0f / temp0.z;
    // mad_sat r0.x, r0.y, r0.z, r0.x
    temp0.x = saturate(temp0.y * temp0.z + temp0.x);
    // mul r0.x, r0.x, c0.y
    temp0.x = temp0.x * ref_end_amount_fade.y;
    // mul r0.x, r0.x, r0.x
    temp0.x = temp0.x * temp0.x;
    // mad oC0, r0.x, c1.z, c1.z
    out_color = temp0.x * float4(0.5, 0.5, 0.5, 0.5) + float4(0.5, 0.5, 0.5, 0.5);
    // 

    return out_color;
}

