    temp2.x = temp0.x * i.texcoord.x;
    // rcp r0.x, c5.y
    temp0.x = 1.0f / filter_color_texture_size.y;
    // mul r0.x, r0.x, r0.w
    temp0.x = temp0.x * temp0.w;
