    temp1.x = -temp0.z + float1(0.01);
    // texld r2, v0, s1
    temp2 = tex2D(self_illumination_texture, texcoord.xy);
    // cmp r1, r1.x, r2, c0.w
    temp1 = (temp1.x >= 0) ? temp2 : float4(0, 0, 0, 0);
    // mul r2, r2, c1.z
    temp2 = temp2 * float4(0.2, 0.2, 0.2, 0.2);
    // add r1, r0, r1
    temp1 = temp0 + temp1;
    // add r0.x, r0.z, c0.y
