};

VertexMain_Output VertexMain(VertexMain_Input i)
{
    VertexMain_Output o;
    float4 temp0;
    float2 temp1, temp2;
    // def c8, 0.15915494, 0.5, 6.2831855, -3.1415927
    // def c9, 1, 0, 0, 0
    // dcl_position v0
    // dcl_texcoord v1
    // dcl_position o0
    // dcl_texcoord o1.xy
    // dcl_texcoord1 o2
    // dp4 o0.w, v0, c3
    o.position.w = dot(i.position, (view_proj_matrix._m03_m13_m23_m33));
